def post(post_id):
	post = posts.get(post_id)
	if request.method == 'GET':
		if post:
			return jsonify(post.to_dict()), 200
		return {'message': 'Post not found'}, 404
	elif request.method == 'DELETE':
		if post:
			del posts[post_id]
			return {'message': 'Post deleted'}, 200
		return {'message': 'Post not found'}, 404

