      "file_contents": "from dataclasses import dataclass, field
import uuid

@dataclass
class Message:
	id: str = field(default_factory=lambda: str(uuid.uuid4()))
	user_id: str
	content: str
	read_receipt: bool = False

	def to_dict(self):
		return {'id': self.id, 'user_id': self.user_id, 'content': self.content, 'read_receipt': self.read_receipt}

@dataclass
class Chat:
	id: str = field(default_factory=lambda: str(uuid.uuid4()))
	name: str
	messages: list = field(default_factory=list)

	def send_message(self, user_id, content):
		message = Message(user_id, content)
		self.messages.append(message)
		return message

	def to_dict(self):
		return {'id': self.id, 'name': self.name, 'messages': [message.to_dict() for message in self.messages]}"
