from dataclasses import dataclass
from datetime import datetime
import random
import string

app = Flask(__name__)

# Mock database
urls = {}
users = {}

@dataclass
class URL:
	original: str
	short: str
	user: str
	clicks: list
	expiration: datetime

@dataclass
class User:
	username: str
	password: str
	urls: list

@app.route('/shorten', methods=['POST'])
def shorten_url():
	data = request.get_json()
	original_url = data.get('url')
	custom_short = data.get('custom')
	username = data.get('username')
	expiration = data.get('expiration')

	# Validate URL
	# TODO: Add actual URL validation

	# Generate short URL
	short_url = ''.join(random.choices(string.ascii_uppercase + string.digits, k=5))
	if custom_short:
		short_url = custom_short

	# Create URL object
	url = URL(original=original_url, short=short_url, user=username, clicks=[], expiration=expiration)

	# Add URL to database
	urls[short_url] = url

	# Add URL to user's list
	if username:
		users[username].urls.append(url)

	return jsonify({'short_url': short_url}), 201

@app.route('/<short_url>', methods=['GET'])
def redirect_to_url(short_url):
	url = urls.get(short_url)
	if not url:
		return jsonify({'error': 'URL not found'}), 404

	# Check expiration
	if url.expiration and url.expiration < datetime.now():
		return jsonify({'error': 'URL expired'}), 410

	# Redirect to original URL
	return redirect(url.original, code=302)

@app.route('/analytics', methods=['GET'])
def get_analytics():
	username = request.args.get('username')
	if not username:
		return jsonify({'error': 'Username required'}), 400

	user = users.get(username)
	if not user:
		return jsonify({'error': 'User not found'}), 404

	# Get analytics for all user's URLs
	analytics = []
	for url in user.urls:
		analytics.append({
			'url': url.short,
			'clicks': len(url.clicks),
			'last_clicked': max(url.clicks, default=None)
		})

	return jsonify(analytics), 200

@app.route('/users', methods=['POST'])
def create_user():
	data = request.get_json()
	username = data.get('username')
	password = data.get('password')

	# Validate input
	if not username or not password:
		return jsonify({'error': 'Username and password required'}), 400

	# Check if user already exists
	if users.get(username):
		return jsonify({'error': 'User already exists'}), 400

	# Create user
	user = User(username=username, password=password, urls=[])

	# Add user to database
	users[username] = user

	return jsonify({'message': 'User created'}), 201

if __name__ == '__main__':
	app.run(debug=True)"
