import string

app = Flask(__name__)

# Mock database
users = {}
urls = {}

@dataclass
class User:
	username: str
	password: str
	urls: dict

@dataclass
class URL:
	original_url: str
	short_url: str
	clicks: int
	click_data: list
	expiration: datetime.datetime

@app.route('/register', methods=['POST'])
def register():
	data = request.get_json()
	username = data['username']
	password = data['password']
	if username in users:
		return {'message': 'Username already exists'}, 400
	users[username] = User(username, password, {})
	return {'message': 'User registered successfully'}, 200

@app.route('/login', methods=['POST'])
def login():
	data = request.get_json()
	username = data['username']
	password = data['password']
	if username not in users or users[username].password != password:
		return {'message': 'Invalid username or password'}, 400
	return {'message': 'Logged in successfully'}, 200

@app.route('/shorten', methods=['POST'])
def shorten():
	data = request.get_json()
	username = data['username']
	original_url = data['original_url']
	short_url = data.get('short_url', ''.join(random.choices(string.ascii_letters + string.digits, k=5)))
	expiration = data.get('expiration', None)
	if expiration:
		expiration = datetime.datetime.strptime(expiration, '%Y-%m-%d %H:%M:%S')
	if short_url in urls:
		return {'message': 'Short URL already exists'}, 400
	try:
		response = requests.get(original_url)
		if response.status_code != 200:
			return {'message': 'Invalid URL'}, 400
	except:
		return {'message': 'Invalid URL'}, 400
	urls[short_url] = URL(original_url, short_url, 0, [], expiration)
	users[username].urls[short_url] = urls[short_url]
	return {'message': 'URL shortened successfully', 'short_url': short_url}, 200

@app.route('/<short_url>', methods=['GET'])
def redirect_to_original(short_url):
	if short_url not in urls or (urls[short_url].expiration and urls[short_url].expiration < datetime.datetime.now()):
		return {'message': 'Invalid or expired URL'}, 400
	urls[short_url].clicks += 1
	geolocator = Nominatim(user_agent='url_shortener')
	location = geolocator.reverse([request.remote_addr])
	urls[short_url].click_data.append({'time': datetime.datetime.now().strftime('%Y-%m-%d %H:%M:%S'), 'location': location})
	return redirect(urls[short_url].original_url, code=302)

@app.route('/analytics', methods=['GET'])
def analytics():
	data = request.get_json()
	username = data['username']
	short_url = data['short_url']
	if short_url not in users[username].urls:
		return {'message': 'Invalid URL'}, 400
	url = users[username].urls[short_url]
	return {'clicks': url.clicks, 'click_data': url.click_data}, 200

