def register():
	data = request.get_json()
	if data['email'] in users:
		return jsonify({'message': 'Email already registered'}), 400
	users[data['email']] = {'password': data['password'], 'profile': {}, 'contacts': [], 'blocked': []}
	return jsonify({'message': 'User registered successfully'}), 200

@app.route('/login', methods=['POST'])
def login():
	data = request.get_json()
	if data['email'] not in users or users[data['email']]['password'] != data['password']:
		return jsonify({'message': 'Invalid email or password'}), 400
	return jsonify({'message': 'Logged in successfully'}), 200

@app.route('/forgot_password', methods=['POST'])
def forgot_password():
	data = request.get_json()
	if data['email'] not in users:
		return jsonify({'message': 'Email not registered'}), 400
	users[data['email']]['password'] = data['new_password']
	return jsonify({'message': 'Password updated successfully'}), 200

@app.route('/update_profile', methods=['POST'])
def update_profile():
	data = request.get_json()
	if data['email'] not in users:
		return jsonify({'message': 'Email not registered'}), 400
	users[data['email']]['profile'] = data['profile']
	return jsonify({'message': 'Profile updated successfully'}), 200

@app.route('/block_contact', methods=['POST'])
def block_contact():
	data = request.get_json()
	if data['email'] not in users:
		return jsonify({'message': 'Email not registered'}), 400
	if data['contact'] not in users[data['email']]['contacts']:
		return jsonify({'message': 'Contact not found'}), 400
	users[data['email']]['blocked'].append(data['contact'])
	users[data['email']]['contacts'].remove(data['contact'])
	return jsonify({'message': 'Contact blocked successfully'}), 200

@app.route('/unblock_contact', methods=['POST'])
def unblock_contact():
	data = request.get_json()
	if data['email'] not in users:
		return jsonify({'message': 'Email not registered'}), 400
	if data['contact'] not in users[data['email']]['blocked']:
		return jsonify({'message': 'Contact not blocked'}), 400
	users[data['email']]['contacts'].append(data['contact'])
	users[data['email']]['blocked'].remove(data['contact'])
	return jsonify({'message': 'Contact unblocked successfully'}), 200

@app.route('/create_group', methods=['POST'])
def create_group():
	data = request.get_json()
	if data['email'] not in users:
		return jsonify({'message': 'Email not registered'}), 400
	group_id = len(groups) + 1
	groups[group_id] = {'name': data['name'], 'picture': data['picture'], 'members': [data['email']], 'admins': [data['email']]}
	return jsonify({'message': 'Group created successfully', 'group_id': group_id}), 200

@app.route('/send_message', methods=['POST'])
def send_message():
	data = request.get_json()
	if data['sender'] not in users:
		return jsonify({'message': 'Sender not registered'}), 400
	if 'group_id' in data:
		if data['group_id'] not in groups or data['sender'] not in groups[data['group_id']]['members']:
			return jsonify({'message': 'Group not found or sender not a member'}), 400
		message_id = len(messages) + 1
		messages[message_id] = {'sender': data['sender'], 'content': data['content'], 'group_id': data['group_id'], 'read_by': []}
	else:
		if data['receiver'] not in users or data['receiver'] in users[data['sender']]['blocked']:
			return jsonify({'message': 'Receiver not registered or blocked'}), 400
		message_id = len(messages) + 1
		messages[message_id] = {'sender': data['sender'], 'content': data['content'], 'receiver': data['receiver'], 'read': False}
	return jsonify({'message': 'Message sent successfully', 'message_id': message_id}), 200

