	data = request.get_json()
	if data['message_id'] not in messages:
		return jsonify({'message': 'Message not found'}), 400
	if 'group_id' in messages[data['message_id']]:
		if data['email'] not in groups[messages[data['message_id']]['group_id']]['members']:
