			self.messages.append(message)
			users_db[receiver_email].messages.append(message)
			return jsonify({'message': 'Message sent successfully'}), 201
		else:
			return jsonify({'message': 'Receiver does not exist'}), 404

	def read_message(self, sender_email):
		for message in self.messages:
			if message.sender == sender_email:
				message.read_status = True
				return jsonify({'message': 'Message read successfully'}), 200
		return jsonify({'message': 'Message not found'}), 404

	def post_status(self, content, visibility_duration, visibility_settings):
		status = Status(content, visibility_duration, visibility_settings)
		self.statuses.append(status)
		return jsonify({'message': 'Status posted successfully'}), 201

	def view_status(self, status_index):
		if status_index < len(self.statuses):
			status = self.statuses[status_index]
			return jsonify({'content': status.content, 'visibility_duration': status.visibility_duration, 'visibility_settings': status.visibility_settings}), 200
		else:
			return jsonify({'message': 'Status not found'}), 404

