from flask import Flask, render_template, request, redirect, url_for, session
from models import User
from services import redirect_url, get_url_analytics

app = Flask(__name__)
app.secret_key = 'secret_key'

users = {}

@app.route('/register', methods=['GET', 'POST'])
def register():
	if request.method == 'POST':
		username = request.form['username']
		password = request.form['password']
		if username not in users:
			users[username] = User(username, password)
			session['username'] = username
			return redirect(url_for('home'))
		else:
			return 'Username already exists!'
	else:
		return render_template('register.html')

@app.route('/login', methods=['GET', 'POST'])
def login():
	if request.method == 'POST':
		username = request.form['username']
		password = request.form['password']
		if username in users and users[username].password == password:
			session['username'] = username
			return redirect(url_for('home'))
		else:
			return 'Invalid username or password!'
	else:
		return render_template('login.html')

@app.route('/logout')
def logout():
	session.pop('username', None)
	return redirect(url_for('home'))

@app.route('/')
def home():
	if 'username' in session:
		return 'Logged in as ' + session['username']
	else:
		return 'You are not logged in!'

@app.route('/<short_url>')
def redirect_short_url(short_url):
	original_url = redirect_url(short_url, users)
	if original_url:
		return redirect(original_url)
	else:
		return 'Invalid short URL!'

@app.route('/user/urls')
def user_urls():
	if 'username' in session:
		user = users[session['username']]
		return {'urls': [url.shortened_url for url in user.urls]}
	else:
		return 'You are not logged in!'

@app.route('/user/urls/<short_url>/edit', methods=['GET', 'POST'])
def edit_short_url(short_url):
	if 'username' in session:
		user = users[session['username']]
		for url in user.urls:
			if url.shortened_url == short_url:
				if request.method == 'POST':
					new_url = request.form['url']
					url.original_url = new_url
					return 'URL updated!'
				else:
					return render_template('edit_url.html', url=url)
		return 'URL not found!'
	else:
		return 'You are not logged in!'

@app.route('/user/urls/<short_url>/delete')
def delete_short_url(short_url):
	if 'username' in session:
		user = users[session['username']]
		for url in user.urls:
			if url.shortened_url == short_url:
				user.urls.remove(url)
				return 'URL deleted!'
		return 'URL not found!'
	else:
		return 'You are not logged in!'

@app.route('/user/urls/<short_url>/analytics')
def url_analytics(short_url):
	if 'username' in session:
		user = users[session['username']]
		for url in user.urls:
			if url.shortened_url == short_url:
				return get_url_analytics(short_url, users)
		return 'URL not found!'
	else:
		return 'You are not logged in!'

