from models import URL, ClickEvent
from utils import validate_url, generate_short_url


def shorten_url(original_url, user, custom_short_url=None):
	if not validate_url(original_url):
		return 'Invalid URL!'

	shortened_url = custom_short_url if custom_short_url else generate_short_url()

	url = URL(original_url, shortened_url, user, creation_date=None, expiration_date=None)
	user.urls.append(url)

	return shortened_url


def redirect_url(short_url, users):
	for user in users.values():
		for url in user.urls:
			if url.shortened_url == short_url:
				click_event = ClickEvent(date_time=None, location=None)  # TODO: Get current date/time and location
				url.click_events.append(click_event)
				return url.original_url
	return None


def get_url_analytics(short_url, users):
	for user in users.values():
		for url in user.urls:
			if url.shortened_url == short_url:
				click_events = url.click_events
				clicks = len(click_events)
				click_dates = [click_event.date_time for click_event in click_events]
				click_locations = [click_event.location for click_event in click_events]
				return {'clicks': clicks, 'click_dates': click_dates, 'click_locations': click_locations}
	return None
