from flask import Flask, render_template, request
import hashlib
import time

app = Flask(__name__)

# Mock databases
users_db = {}
messages_db = {}
groups_db = {}
statuses_db = {}
queued_messages_db = {}

@app.route('/')
def home():
	return render_template('home.html')

@app.route('/signup', methods=['GET', 'POST'])
def signup():
	if request.method == 'POST':
		# Get user data from form
		user_data = request.form.to_dict()
		# Check if email is already in use
		if user_data['email'] in users_db:
			return 'Email already in use'
		# Store user data in mock database
		users_db[user_data['email']] = user_data
		users_db[user_data['email']]['blocked_contacts'] = []
		users_db[user_data['email']]['groups'] = []
		# Initialize messages and queued messages for user
		messages_db[user_data['email']] = []
		queued_messages_db[user_data['email']] = []
		return 'User registered successfully'
	return render_template('signup.html')

@app.route('/login', methods=['GET', 'POST'])
def login():
	if request.method == 'POST':
		# Get user data from form
		user_data = request.form.to_dict()
		# Check if user exists in mock database
		if user_data['email'] in users_db and users_db[user_data['email']]['password'] == user_data['password']:
			return 'User logged in successfully'
		return 'Invalid email or password'
	return render_template('login.html')

@app.route('/forgot_password', methods=['GET', 'POST'])
def forgot_password():
	if request.method == 'POST':
		# Get user email from form
		email = request.form['email']
		# Check if user exists in mock database
		if email in users_db:
			# Reset password
			users_db[email]['password'] = 'new_password'
			return 'Password reset link has been sent to your email'
		return 'Email not found'
	return render_template('forgot_password.html')

@app.route('/profile', methods=['GET', 'POST'])
def profile():
	if request.method == 'POST':
		# Get user data from form
		user_data = request.form.to_dict()
		# Check if user exists in mock database
		if user_data['email'] in users_db:
			# Update user profile
			users_db[user_data['email']]['profile_picture'] = user_data['profile_picture']
			users_db[user_data['email']]['status_message'] = user_data['status_message']
			users_db[user_data['email']]['privacy_settings'] = user_data['privacy_settings']
			return 'Profile updated successfully'
		return 'User not found'
	return render_template('profile.html')

@app.route('/send_message', methods=['POST'])
def send_message():
	# Get message data from form
	message_data = request.form.to_dict()
	# Check if recipient is online
	if statuses_db[message_data['recipient']] == 'online':
		# Send message
		messages_db[message_data['recipient']].append(message_data['message'])
	else:
		# Queue message
		queued_messages_db[message_data['recipient']].append(message_data['message'])
	return 'Message sent'

@app.route('/update_status', methods=['POST'])
def update_status():
	# Get status data from form
	status_data = request.form.to_dict()
	# Update user status
	statuses_db[status_data['email']] = status_data['status']
	# Send queued messages if user is online
	if status_data['status'] == 'online' and status_data['email'] in queued_messages_db:
		messages_db[status_data['email']].extend(queued_messages_db[status_data['email']])
		queued_messages_db[status_data['email']].clear()
	return 'Status updated'

if __name__ == '__main__':
	app.run(debug=True)
