from models import User, Post

views = Blueprint('views', __name__)

users = {}
posts = {}

@views.route('/register', methods=['POST'])
def register():
	data = request.get_json()
	if data['username'] in users:
		return jsonify({'message': 'Username already exists'}), 400
	users[data['username']] = User(len(users), data['email'], data['username'], data['password'], '', '', '', '')
	return jsonify({'message': 'User registered successfully'}), 200

@views.route('/login', methods=['POST'])
def login():
	data = request.get_json()
	user = users.get(data['username'])
	if user and user.check_password(data['password']):
		return jsonify({'message': 'Login successful'}), 200
	return jsonify({'message': 'Invalid username or password'}), 400

@views.route('/profile/<username>', methods=['GET'])
def profile(username):
	user = users.get(username)
	if user:
		return jsonify(user.__dict__), 200
	return jsonify({'message': 'User not found'}), 404

@views.route('/post', methods=['POST'])
def post():
	data = request.get_json()
	user = users.get(data['username'])
	if user:
		post = Post(len(posts), user.id, data['text'], data['image'])
		user.posts.append(post)
		posts[post.id] = post
		return jsonify({'message': 'Post created'}), 200
	return jsonify({'message': 'User not found'}), 404

@views.route('/posts/<username>', methods=['GET'])
def user_posts(username):
	user = users.get(username)
	if user:
		return jsonify([post.__dict__ for post in user.posts]), 200
	return jsonify({'message': 'User not found'}), 404

