from dataclasses import dataclass
from datetime import datetime
import uuid
import validators

app = Flask(__name__)

# Mock database
DB = {}

@dataclass
class URL:
	original: str
	shortened: str
	user: str
	clicks: list
	expiration: datetime

@app.route('/shorten', methods=['POST'])
def shorten_url():
	data = request.get_json()
	original_url = data.get('url')
	user = data.get('user')
	expiration = data.get('expiration')
	if not validators.url(original_url):
		return jsonify({'error': 'Invalid URL'}), 400
	if not user:
		return jsonify({'error': 'User name is required'}), 400
	shortened_url = str(uuid.uuid4())[:8]
	DB[shortened_url] = URL(original_url, shortened_url, user, [], expiration)
	return jsonify({'shortened_url': shortened_url}), 201

@app.route('/<short_url>', methods=['GET'])
def redirect_url(short_url):
	url = DB.get(short_url)
	if url and datetime.now() <= url.expiration:
		url.clicks.append((str(datetime.now()), request.remote_addr))
		return redirect(url.original, code=302)
	else:
		return jsonify({'error': 'URL not found or expired'}), 404

@app.route('/clicks/<short_url>', methods=['GET'])
def get_clicks(short_url):
	url = DB.get(short_url)
	if url:
		return jsonify({'clicks': len(url.clicks)}), 200
	else:
		return jsonify({'error': 'URL not found'}), 404

if __name__ == '__main__':
	app.run(debug=True)"
