from dataclasses import dataclass
import json

app = Flask(__name__)

# Mock database
users = {}
messages = {}

@dataclass
class User:
	name: str
	email: str
	password: str
	profile_picture: str = None
	status_message: str = None
	privacy_settings: dict = None

@dataclass
class Message:
	from_user: str
	to_user: str
	message: str
	read: bool = False

@app.route('/signup', methods=['POST'])
def signup():
	data = request.get_json()
	user = User(**data)
	users[user.email] = user
	return jsonify({'message': 'User created'}), 201

@app.route('/login', methods=['POST'])
def login():
	data = request.get_json()
	user = users.get(data['email'])
	if user and user.password == data['password']:
		return jsonify({'message': 'Logged in'}), 200
	return jsonify({'message': 'Invalid credentials'}), 401

@app.route('/message', methods=['POST'])
def send_message():
	data = request.get_json()
	message = Message(**data)
	messages.setdefault(message.from_user, []).append(message)
	return jsonify({'message': 'Message sent'}), 201

@app.route('/message', methods=['GET'])
def get_messages():
	from_user = request.args.get('from_user')
	to_user = request.args.get('to_user')
	user_messages = messages.get(from_user, [])
	user_messages = [m for m in user_messages if m.to_user == to_user]
	return jsonify([m.__dict__ for m in user_messages]), 200

if __name__ == '__main__':
	app.run(debug=True)"
