from dataclasses import dataclass

app = Flask(__name__)

# Mock database
DB = {
	'users': {},
	'recipes': {}
}

@dataclass
class User:
	id: str
	name: str
	recipes: list
	favorites: list
	followers: list

@dataclass
class Recipe:
	id: str
	name: str
	ingredients: list
	instructions: list
	images: list
	categories: list
	reviews: list

@app.route('/user', methods=['POST'])
def create_user():
	data = request.get_json()
	user = User(**data)
	DB['users'][user.id] = user
	return jsonify(user), 201

@app.route('/recipe', methods=['POST'])
def create_recipe():
	data = request.get_json()
	recipe = Recipe(**data)
	DB['recipes'][recipe.id] = recipe
	return jsonify(recipe), 201

@app.route('/recipe/<recipe_id>', methods=['PUT'])
def update_recipe(recipe_id):
	data = request.get_json()
	recipe = DB['recipes'].get(recipe_id)
	if not recipe:
		return jsonify({'message': 'Recipe not found'}), 404
	for key, value in data.items():
		setattr(recipe, key, value)
	DB['recipes'][recipe_id] = recipe
	return jsonify(recipe), 200

@app.route('/recipe/<recipe_id>', methods=['DELETE'])
def delete_recipe(recipe_id):
	recipe = DB['recipes'].get(recipe_id)
	if not recipe:
		return jsonify({'message': 'Recipe not found'}), 404
	del DB['recipes'][recipe_id]
	return jsonify({'message': 'Recipe deleted'}), 200

@app.route('/recipe/search', methods=['GET'])
def search_recipe():
	query = request.args.get('q')
	results = [recipe for recipe in DB['recipes'].values() if query in recipe.name or query in recipe.ingredients or query in recipe.categories]
	return jsonify(results), 200

@app.route('/user/<user_id>/follow', methods=['POST'])
def follow_user(user_id):
	follower_id = request.args.get('follower_id')
	user = DB['users'].get(user_id)
	if not user:
		return jsonify({'message': 'User not found'}), 404
	user.followers.append(follower_id)
	DB['users'][user_id] = user
	return jsonify(user), 200

if __name__ == '__main__':
	app.run(debug=True)"
