from dataclasses import dataclass

app = Flask(__name__)

# Mock database
users = {}
recipes = {}

@dataclass
class User:
	id: str
	name: str
	recipes: list
	favorites: list

@dataclass
class Recipe:
	id: str
	name: str
	ingredients: list
	instructions: list
	images: list
	categories: list
	reviews: list

@app.route('/user', methods=['POST'])
def create_user():
	data = request.get_json()
	user = User(**data)
	users[user.id] = user
	return jsonify(user), 201

@app.route('/recipe', methods=['POST'])
def create_recipe():
	data = request.get_json()
	recipe = Recipe(**data)
	recipes[recipe.id] = recipe
	return jsonify(recipe), 201

@app.route('/user/<user_id>/recipe', methods=['POST'])
def add_recipe(user_id):
	data = request.get_json()
	recipe = Recipe(**data)
	users[user_id].recipes.append(recipe)
	return jsonify(recipe), 201

@app.route('/user/<user_id>/favorite', methods=['POST'])
def add_favorite(user_id):
	data = request.get_json()
	recipe = recipes[data['recipe_id']]
	users[user_id].favorites.append(recipe)
	return jsonify(recipe), 201

@app.route('/recipe/<recipe_id>', methods=['DELETE'])
def delete_recipe(recipe_id):
	recipe = recipes.pop(recipe_id, None)
	if recipe is None:
		return '', 404
	return '', 204

@app.route('/recipe/<recipe_id>', methods=['PUT'])
def update_recipe(recipe_id):
	data = request.get_json()
	recipe = recipes[recipe_id]
	for key, value in data.items():
		setattr(recipe, key, value)
	return jsonify(recipe), 200

@app.route('/recipe/search', methods=['GET'])
def search_recipe():
	query = request.args.get('q')
	results = [recipe for recipe in recipes.values() if query in recipe.name]
	return jsonify(results), 200

if __name__ == '__main__':
	app.run(debug=True)"
    },
