from typing import Dict

app = Flask(__name__)

# Mock database
DATABASE = {}

@dataclass
class BookClub:
	name: str
	description: str
	is_private: bool
	members: Dict[str, bool]

@app.route('/create_club', methods=['POST'])
def create_club():
	data = request.get_json()
	club = BookClub(name=data['name'], description=data['description'], is_private=data['is_private'], members={})
	DATABASE[data['name']] = club
	return jsonify({'message': 'Club created successfully'}), 201

@app.route('/join_club', methods=['POST'])
def join_club():
	data = request.get_json()
	club_name = data['club_name']
	user_name = data['user_name']
	if club_name in DATABASE:
		club = DATABASE[club_name]
		if club.is_private:
			return jsonify({'message': 'This is a private club. Request sent to admin.'}), 200
		else:
			club.members[user_name] = True
			return jsonify({'message': 'Joined the club successfully'}), 200
	else:
		return jsonify({'message': 'Club does not exist'}), 404

if __name__ == '__main__':
	app.run(debug=True)"
