def join_club():
	data = request.get_json()
	user = users.get(data['user_name'])
	club = clubs.get(data['club_name'])
	if not club.is_private:
		club.members.append(user)
		user.clubs.append(club)
		return jsonify({'message': 'Joined club successfully'}), 200
