	def __init__(self, user, text):
		self.user = user
		self.text = text
		self.votes = 0

	def upvote(self):
		self.votes += 1

	def downvote(self):
		self.votes -= 1

class Vote:
	def __init__(self, user, comment, vote_type):
		self.user = user
		self.comment = comment
		self.vote_type = vote_type
